// Import necessary libraries and components
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Validation from './SignupValidation'; // Import the validation function
import axios from 'axios';
import BackgroundImage from '../src/images/bg2.jpg';

// Signup component
function Signup() {
  // Initialize the navigate function from react-router
  const navigate = useNavigate();

  // Initialize state for form values and errors
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    age: '', // New field for age
    address: '' // New field for address
  });

  const [errors, setErrors] = useState({});

  // Handle input changes and update the state
  const handleInput = (event) => {
    setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Validate the form data and set errors
    setErrors(Validation(values));

    if (errors.name === "" && errors.email === "" && errors.password === "") {
      try {
        // If there are no validation errors, make a POST request
        await axios.post('http://localhost:5000/storeUserData', values);

        // Successfully signed up, now navigate to the Home page
        navigate('/Home'); // Use the navigate function to redirect
      } catch (error) {
        console.error('Error submitting data:', error);
      }
    }
  };

  // Return the JSX for the Signup component
  return (
    <div className='d-flex justify-content-center align-items-center vh-100' style={Bgstyle}>
      <div id="reg-form">
        <form action="" onSubmit={handleSubmit} id="reg-form-1">
          {/* ... (existing code for name, email, password) */}

          {/* New input for age */}
          <div className='mb-3'>
            <label htmlFor='age'><strong>Age</strong></label>
            <input
              type='text'
              placeholder='Enter your age'
              name='age'
              onChange={handleInput}
              id='age'
            />
            {/* Add validation error display if needed */}
          </div>

          {/* New input for address */}
          <div className='mb-3'>
            <label htmlFor='address'><strong>Address</strong></label>
            <input
              type='text'
              placeholder='Enter your address'
              name='address'
              onChange={handleInput}
              id='address'
            />
            {/* Add validation error display if needed */}
          </div>

          {/* ... (existing code for submit button, checkbox, and login link) */}
        </form>
      </div>
    </div>
  );
}

// Define the styles for the background
const Bgstyle = {
  background: `url(${BackgroundImage})`, // Set the background image
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
};

// Export the Signup component
export default Signup;
