// UserHomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserHomePage({ location }) {
  const customerId = location.state.customer_id;
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Fetch user data upon component mount (you might want to use authentication tokens for security)
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/user/${customerId}`);
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [customerId]);

  return (
    <div>
      <h2>User Home Page</h2>
      {userData && (
        <div>
          <p>Customer ID: {userData.customer_id}</p>
          <p>Name: {userData.name}</p>
          <p>Email: {userData.email}</p>
          <p>Age: {userData.age}</p>
          <p>Address: {userData.address}</p>
          {/* Add more user details as needed */}
        </div>
      )}
    </div>
  );
}

export default UserHomePage;
