// MakeTransactionPage.jsx

import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './MakeTransactionPage.css'; // Import your CSS file for styling

function MakeTransactionPage() {
    const [amount, setAmount] = useState('');
    const [description, setDescription] = useState('');
    const { customerId } = useParams();

    const handleAmountChange = (event) => {
        setAmount(event.target.value);
    };

    const handleDescriptionChange = (event) => {
        setDescription(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        // Add logic to handle the transaction submission
        console.log(`Transaction Details - Amount: ${amount}, Description: ${description}`);
    };

    return (
        <div className="make-transaction-page">
            <h2>Make Transaction</h2>
            <form onSubmit={handleSubmit}>
                <div className="transaction-input">
                    <label htmlFor="amount">Amount</label>
                    <input
                        type="number"
                        id="amount"
                        name="amount"
                        value={amount}
                        onChange={handleAmountChange}
                        placeholder="Enter amount"
                        required
                    />
                </div>
                <div className="transaction-input">
                    <label htmlFor="description">Description</label>
                    <input
                        type="text"
                        id="description"
                        name="description"
                        value={description}
                        onChange={handleDescriptionChange}
                        placeholder="Enter description"
                        required
                    />
                </div>
                <div className="transaction-button">
                    <button type="submit">Submit</button>
                </div>
            </form>
        </div>
    );
}

export default MakeTransactionPage;
