import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [values, setValues] = useState({
        customerId: '',
        password: ''
    });

    const navigate = useNavigate();

    const handleInput = (event) => {
        setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        // Make a POST request for login
        axios.post('http://localhost:5000/login', values)
            .then(response => {
                // Check the response from the server
                if (response.status === 200) {
                    // Successfully logged in, redirect to UserHomePage with customerId
                    navigate('/user', { state: { customerId: response.data.customer_id } });
                } else {
                    // Handle other cases, show error messages, etc.
                    console.log('Login failed:', response.data.message);
                }
            })
            .catch(err => {
                console.log('Login error:', err);
                // Handle other errors, show error messages, etc.
            });
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="customerId">Customer ID:</label>
                <input
                    type="text"
                    id="customerId"
                    name="customerId"
                    value={values.customerId}
                    onChange={handleInput}
                />
                <label htmlFor="password">Password:</label>
                <input
                    type="password"
                    id="password"
                    name="password"
                    value={values.password}
                    onChange={handleInput}
                />
                <button type="submit">Login</button>
            </form>
            <p>New User? <Link to="/Signup">Create</Link> an account</p>
        </div>
    );
}

export default Login;
