// Login.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import BackgroundImage from '../src/images/bg1.jpg';
import userImage from '../src/images/user.png';
import Validation from './Validation'; // Import the modified Validation function
import bcrypt from 'bcryptjs';

function Login() {
    const [values, setValues] = useState({
        customerId: '',
        password: ''
    });

    const [errors, setErrors] = useState({}); // Define errors state

    const navigate = useNavigate();

    const handleInput = (event) => {
        setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
    
        // Hash the password before sending it
        const hashedPassword = bcrypt.hashSync(values.password, 10);
    
        // Update the values with the hashed password
        setValues(prev => ({ ...prev, password: hashedPassword }));
    
        // Make a POST request for login
        axios.post('http://localhost:5000/login', values)
            .then(response => {
                // Check the response from the server
                if (response.status === 200) {
                    // Successfully logged in, redirect to UserHomePage with customerId
                    navigate('/user', { state: { customerId: response.data.customer_id } });
                } else {
                    // Handle other cases, show error messages, etc.
                    console.log('Login failed:', response.data.message);
                }
            })
            .catch(err => {
                console.log('Login error:', err);
                // Handle other errors, show error messages, etc.
            });
    };

    const Bgstyle = {
        background: `url(${BackgroundImage})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    };

    return (
        <div className='d-flex justify-content-center align-items-center vh-100' style={Bgstyle}>
            <div id='form'>
                <img src={userImage} alt='User' className='user-image' />
                <form action='' onSubmit={handleSubmit} id='form1'>
                    <h2 className='text-center' id='h2text'>
                        LOGIN
                    </h2>
                    <div className='mb-3'>
                        <label htmlFor='customerId'>
                            <strong>Customer ID</strong>
                        </label>
                        <div className='input-container'>
                            <input
                                type='text'
                                placeholder='Enter customer ID'
                                name='customerId'
                                onChange={handleInput}
                                id='customerId'
                            />
                        </div>
                        {/* Display validation error for customerId */}
                        {errors.customerId && <p className='text-danger'>{errors.customerId}</p>}
                    </div>

                    <div className='mb-3'>
                        <label htmlFor='password'>
                            <strong>Password</strong>
                        </label>
                        <div className='input-container'>
                            <input
                                type='password'
                                placeholder='Enter password'
                                name='password'
                                onChange={handleInput}
                                id='password'
                            />
                        </div>
                        {/* Display validation error for password */}
                        {errors.password && <p className='text-danger'>{errors.password}</p>}
                    </div>
                    <button type='submit' className='btn btn-success w-100' id='login-btn'>
                        <strong>Login</strong>
                    </button>
                    <p></p>
                    <p id='p'>
                        New User? <Link to='/Signup'>Create</Link> an account
                    </p>
                </form>
            </div>
        </div>
    );
}

export default Login;
