// MakeTransactionPage.jsx

import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './MakeTransactionPage.css'; // Import your CSS file for styling

function MakeTransactionPage() {
    const [senderId, setSenderId] = useState('');
    const [senderPassword, setSenderPassword] = useState('');
    const [receiverId, setReceiverId] = useState('');
    const [amount, setAmount] = useState('');
    const [reason, setReason] = useState('');
    const { customerId } = useParams();

    const handleInputChange = (setter) => (event) => {
        setter(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Assuming you have a backend API endpoint for handling transactions
        try {
            const response = await axios.post(`http://localhost:5000/transactions/${customerId}`, {
                senderId,
                senderPassword,
                receiverId,
                amount,
                reason,
                customerId, // Assuming you want to associate the transaction with the logged-in user
            });

            // Handle successful transaction response
            console.log('Transaction successful:', response.data);
        } catch (error) {
            // Handle transaction failure
            console.error('Transaction failed:', error.response.data);
        }
    };

    return (
        <div className="make-transaction-page">
            <h2>Make Transaction</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <div>
                        <label htmlFor="senderId">Sender's User ID</label>
                        <input
                            type="text"
                            id="senderId"
                            name="senderId"
                            value={senderId}
                            onChange={handleInputChange(setSenderId)}
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="senderPassword">Sender's Password</label>
                        <input
                            type="password"
                            id="senderPassword"
                            name="senderPassword"
                            value={senderPassword}
                            onChange={handleInputChange(setSenderPassword)}
                            required
                        />
                    </div>
                </div>
                <div className="form-group">
                    <div>
                        <label htmlFor="receiverId">Receiver's Transaction ID</label>
                        <input
                            type="text"
                            id="receiverId"
                            name="receiverId"
                            value={receiverId}
                            onChange={handleInputChange(setReceiverId)}
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="amount">Amount</label>
                        <input
                            type="number"
                            id="amount"
                            name="amount"
                            value={amount}
                            onChange={handleInputChange(setAmount)}
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="reason">Reason for Transaction</label>
                        <input
                            type="text"
                            id="reason"
                            name="reason"
                            value={reason}
                            onChange={handleInputChange(setReason)}
                            required
                        />
                    </div>
                </div>
                <div className="transaction-button">
                    <button type="submit">Submit Transaction</button>
                </div>
            </form>
        </div>
    );
}

export default MakeTransactionPage;
