import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import BackgroundImage from '../src/images/bg1.jpg';
import userImage from '../src/images/user.png';

function Login() {
  // Initialize the state for form values and errors
  const [values, setValues] = useState({
    customerId: '',
    password: '',
  });

  // Initialize the navigate function from react-router
  const navigate = useNavigate();

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Make a POST request for login
      const response = await axios.post('http://localhost:5000/login', values);

      // Check the response from the server
      if (response.status === 200) {
        // Successfully logged in, redirect to Home
        navigate('/Home');
      } else {
        // Handle other cases, show error messages, etc.
        console.log('Login failed:', response.data.message);
      }
    } catch (error) {
      console.log('Login error:', error);
      // Handle other errors, show error messages, etc.
    }
  };

  // Handle input changes
  const handleInput = (event) => {
    // Update the state when input fields change
    setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
  };

  return (
    <div className='d-flex justify-content-center align-items-center vh-100' style={Bgstyle}>
      <div id='form'>
        <img src={userImage} alt='User' className='user-image' />
        <form action='' onSubmit={handleSubmit} id='form1'>
          <h2 className='text-center' id='h2text'>
            LOGIN
          </h2>
          <div className='mb-3'>
            <label htmlFor='customerId'>
              <strong>Customer ID</strong>
            </label>
            <div className='input-container'>
              <input
                type='text'
                placeholder='Enter customer ID'
                name='customerId'
                onChange={handleInput}
                id='customerId'
              />
            </div>
          </div>

          <div className='mb-3'>
            <label htmlFor='password'>
              <strong>Password</strong>
            </label>
            <div className='input-container'>
              <input
                type='password'
                placeholder='Enter password'
                name='password'
                onChange={handleInput}
                id='password'
              />
            </div>
          </div>
          <button type='submit' className='btn btn-success w-100' id='login-btn'>
            <strong>Login</strong>
          </button>
          <p></p>
          <p id='p'>
            New User? <Link to='/Signup'>Create</Link> an account
          </p>
        </form>
      </div>
    </div>
  );
}

const Bgstyle = {
  background: `url(${BackgroundImage})`,
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
  backgroundPosition: 'center',
};

export default Login;
