import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserHomePage({ match }) {
    customerId = user.Customer_id;
     customerId = match.params.customerId;
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Check if customerId exists in the URL
        if (!customerId) {
            setError('Invalid URL. Please provide a valid customerId.');
            setLoading(false);
            return;
        }

        // Fetch user data based on customerId
        axios.get(`http://localhost:5000/user/${customerId}`)
            .then(response => {
                setUser(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching user details:', error);
                setError('Error fetching user details. Please try again later.');
                setLoading(false);
            });
    }, [customerId]);

    return (
        <div>
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            {user && (
                <div>
                    <h1>Welcome, {user.name}!</h1>
                    <p>Email: {user.email}</p>
                    <p>Age: {user.age}</p>
                    <p>Address: {user.address}</p>
                    {/* Add more user details as needed */}
                </div>
            )}
        </div>
    );
}

export default UserHomePage;
