// UserHomePage.js
import React, { useState, useEffect } from 'react';

function UserHomePage({ location }) {
    // Use state to manage user details
    const [user, setUser] = useState(null);

    // Fetch user details when the component mounts
    useEffect(() => {
        // Fetch user details from the server or use the passed user object
        // Example: Replace the following line with your actual data fetching logic
        // fetchUserData(location.state.userId).then(userData => setUser(userData));

        // Simulating data fetching with a delay
        const fetchData = async () => {
            try {
                // Simulating an API call or any asynchronous operation
                // Replace this with your actual data fetching logic
                const response = await fetch(`http://localhost:5000/getUserData/${location.state.userId}`);
                const userData = await response.json();

                // Update the state with the fetched user data
                setUser(userData);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        fetchData();
    }, [location.state.userId]); // Run the effect whenever location.state.userId changes

    // Render user details
    return (
        <div>
            {user ? (
                <div>
                    <h1>Welcome, {user.name}!</h1>
                    <p>Email: {user.email}</p>
                    <p>Age: {user.age}</p>
                    <p>Address: {user.address}</p>
                    {/* Add more user details as needed */}
                </div>
            ) : (
                <p>Loading user details...</p>
            )}
        </div>
    );
}

export default UserHomePage;
