import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Validation from './LoginValidation';
import axios from 'axios';
import BackgroundImage from '../src/images/bg1.jpg';
import userImage from '../src/images/user.png';

function Login() {
    // Initialize the state for form values and errors
    const [values, setValues] = useState({
        customerId: '', // Change from 'email' to 'customerId'
        password: ''
    })

    // Initialize the navigate function from react-router
    const navigate = useNavigate();

    const [errors, setErrors] = useState({});

    const handleInput = (event) => {
        // Update the state when input fields change
        setValues(prev => ({ ...prev, [event.target.name]: event.target.value }));
    }

    // ... (previous code)

// Handle form submission
// Handle form submission
const handleSubmit = (event) => {
    event.preventDefault();
  
    // Make a POST request for login
    axios.post('http://localhost:5000/login', values)
      .then(response => {
        // Check the response from the server
        if (response.status === 200) {
          // Successfully logged in, redirect to Home
          navigate('/Home');
        } else {
          // Handle other cases, show error messages, etc.
          console.log('Login failed:', response.data.message);
        }
      })
      .catch(err => {
        console.log('Login error:', err);
        // Handle other errors, show error messages, etc.
      });
};

  
  // ... (rest of the code)
  

    return (
        <div className='d-flex justify-content-center align-items-center vh-100' style={Bgstyle}>
            <div id="form">
                <img src={userImage} alt="User" className="user-image" />
                <form action="" onSubmit={handleSubmit} id="form1">
                    <h2 className="text-center" id="h2text">LOGIN</h2>
                    <div className='mb-3'>
                        <label htmlFor="customerId"><strong>Customer ID</strong></label>
                        <div className="input-container">
                            <input
                                type='text' // Assuming it's a text input for customer ID
                                placeholder='Enter customer ID'
                                name='customerId'
                                onChange={handleInput}
                                id='name' // Use id instead of name
                            />
                            {errors.customerId && <span className='text-danger'>{errors.customerId}</span>}
                        </div>
                    </div>

                    <div className='mb-3'>
                        <label htmlFor="password"><strong>Password</strong></label>
                        <div className="input-container">
                            <input
                                type='password'
                                placeholder='Enter password'
                                name='password'
                                onChange={handleInput}
                                id='password' // Use id instead of name
                            />
                            {errors.password && <span className='text-danger'>{errors.password}</span>}
                        </div>
                    </div>
                    <button type='submit' className='btn btn-success w-100' id="login-btn"><strong>Login</strong></button>
                    <p></p>
                    <p id='p'>New User? <Link to="/Signup">Create</Link> an account</p>
                </form>
            </div>
        </div>
    )
}

const Bgstyle = {
    background: `url(${BackgroundImage})`, // Set the background image
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
}

export default Login;
